import React from 'react';


class App extends React.Component {

  constructor() {
    super();
    this.handleButtonOne = this.handleButtonOne.bind(this);
    this.handleButtonTwo = this.handleButtonTwo.bind(this);
    this.handleButtonThree = this.handleButtonThree.bind(this);
    this.state = {
      clicksBtnOne: 0,
      clicksBtnTwo: 0,
      clicksBtnThree: 0,
    };
  }
  handleButtonOne() {
    this.setState((estado, _props)=> {
      clicksBtnOne: estado.clicksBtnOne + 1
    })
  }

  handleButtonTwo() {
    this.setState((estado, _props)=> {
      clicksBtnTwo: estado.clicksBtnOne + 1
    })
  }

  handleButtonThree() {
    this.setState((estado, _props)=> {
      clicksBtnThree: estado.clicksBtnOne + 1
    })
  }

  render() {
    return (<><button onClick={this.handleButtonOne}>Botão 1</button>
              <button onClick={this.handleButtonTwo}>Botão 2</button>
              <button onClick={this.handleButtonThree}>Botão 3</button></>);
  }
};

export default App;
